int* getStates(char *sequence, int repeats) {
    int n = strlen(sequence);
    int *states = (int*) malloc(sizeof(int) * n * repeats);

    int k = 0;
    char *el;
    for (int r = 0; r < repeats; ++r) {
        for (int i = 0; i < n; i++) {
            el = std::find(std::begin(aminoAcids), std::end(aminoAcids), sequence[i]);
            if(el == std::end(aminoAcids)){
                states[k++] = 20;
            } else {
                states[k++] = std::distance(aminoAcids, el);
            }
        }
    }
    return states;
}

